operation.add:
if (!isset($this->request->cookie['tracking'])) {
	setcookie('tracking', $coupon, time() + 3600 * 24 * 1000, '/');
	$this->db->query("UPDATE `" . DB_PREFIX . "marketing` SET clicks = (clicks + 1) WHERE code = '" . $this->db->escape($coupon) . "'");
}

